function getTimeRemaining(e){var n=Date.parse(e)-Date.parse(new Date),a=pad(Math.floor(n/1e3%60)),t=pad(Math.floor(n/1e3/60%60)),o=pad(Math.floor(n/36e5%24)),i=pad(Math.floor(n/864e5));return{total:n,days:i,hours:o,minutes:t,seconds:a}}function initializeClock(e,n){var a=document.getElementById(e),t=setInterval(function(){var e=getTimeRemaining(n);a.innerHTML=e.days+" : "+e.hours+" : "+e.minutes+" : "+e.seconds,e.total<=0&&clearInterval(t)},1e3)}function pad(e){return e<10?"0"+e:e}var deadline="2017-01-27";$(document).ready(function(){initializeClock("countdown",deadline)});